import React, { useState, useEffect } from "react";

import fire from "../fire";

// import map from "../../assets/MIC_Regions";

function Locations() {
  const [loc, setLoc] = useState("");
  
  //holding names of articles
  var locations = {east: [], west: [], central: [], masters: []};

  //getting articles from database
  useEffect(() => {
    fire.firestore().collection('web').doc('sites').get()
      .then((doc) => {
        setLoc(doc.data().sites.records);
      })
      .catch((error) => {
        console.log(error)
      })
    }, [])

  console.log(loc)
  
  for(const i in loc){
    if(loc[i].region === "east")
      locations.east.push(loc[i]);
    else if(loc[i].region === "west")
      locations.west.push(loc[i]);
    else if(loc[i].region === "central")
      locations.central.push(loc[i]);
    else if(loc[i].region === "masters")
      locations.masters.push(loc[i]);
  }

  for(const i in locations){
    locations[i].sort()
  }
  console.log(locations)

  return(
    <div style={{display: "flex", flexDirection:"row"}}>
      <div style={{margin:"2%", boxShadow:"0 3px 1px -2px rgb(0 0 0 / 20%), 0 2px 2px 0 rgb(0 0 0 / 14%), 0 1px 5px 0 rgb(0 0 0 / 12%)", width:"100%"}}>
        <div style={{marginLeft:"1%", marginRight:"1%"}}>
          <h1 style={{fontStyle:"italic"}}>Locations</h1>
          <>
            <h3>WEST</h3>
            {locations.west.map((doc) => {
                return(
                  <p>
                    <a href={doc.mapUrl}>{doc.name}</a> - 
                    {doc.street}, {doc.city}
                  </p>
                );
              })
            }
          </>
        </div>
      </div>
    </div>
  );
}

export default Locations;
