import { TextField, MenuItem, makeStyles, Grid } from "@material-ui/core";
import { Autocomplete } from "@material-ui/lab";
import React, { useState } from "react";

//Options for each dropdown. Probably use JSON for them

let options = require("./options");

const useStyles = makeStyles((theme) => ({
   root: {
     '& .MuiTextField-root': {
       margin: theme.spacing(1),
       width: '25ch',
     },
   },
 }));

// <TextField
// select
// variant="outlined"
// label="Please Select Grade Level"
// value={choice.lev}
// onChange={(event) => onChange(event, "level")}
// >
// {options.level.map((option) => (
//    <MenuItem key={option.value} value={option.value}>
//       {option.label}
//    </MenuItem>
// ))}
// </TextField>

function TeamRegister(){
   const classes = useStyles();
   const [choice, setChoice] = useState({loc: "", lev: "", school: "", coach: ""});

   let longest = 0;

   for(var option in options){
      console.log(option)
      for(let i = 0; i < option.length; i++){
         console.log(option.length)
         if(options[option][i].label.length > longest)
            longest = options[option][i].label.length;
      }
   }

   longest *= 10;
   console.log(longest)

   const onChange = (newValue, type) => {
      switch (type) {
         case "location":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  loc: newValue,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  loc: "",
               }));
            }
            console.log(choice)
            break;
         case "level":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  lev: newValue,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  lev: "",
               }));
            }
            console.log(choice)
            break;
         case "school":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  school: newValue,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  school: "",
               }));
            }
            console.log(choice)
            break;
         case "coach":
            setChoice((prevState) => ({
               ...prevState,
               coach: newValue.target.value,
            }));
            break;
         default:
            console.log(newValue, type)
      }
   };

   return(
      <div style={{paddingLeft:"10px"}}>
         <p>
            <b>Rules for Individuals:</b> Any student may compete as an individual 
            in their grade level or any higher grade; however, a student may 
            compete as a team at one grade level only. This applies to both 
            Championships and Masters.<br/>

            Also note each team includes four students in addition to 
            two alternates per school that can compete as individuals. So 
            when registering n teams , you get to bring 4n+2 students along. 
            These students don't need to be registered as individuals separately.
         </p>
         <form className={classes.root} noValidate autoComplete="off">
            <div style={{display:"flex"}}>
               <Grid item sm={3}>
                  <p>Competition Location</p>
               </Grid>

               <Autocomplete
                  options={options.locations.map((option) => option.label)}
                  getOptionSelected={(option, value) => option.label === value.label}
                  value={choice.loc}
                  onChange={(event, newValue) => onChange(newValue, "location")}
                  
                  renderInput={(params) => 
                                 <TextField 
                                    {...params} 
                                    label="Select Competition Location" 
                                    variant="outlined"
                                    style={{ width: {longest} }}
                                 />
                              }
               />
            </div>
            <div style={{display:"flex"}}>
               <Grid item sm={3}>
                  <p>Competition Level</p>
               </Grid>
               <Autocomplete
                  options={options.level.map((option) => option.label)}
                  getOptionSelected={(option, value) => option.label === value.label}
                  value={choice.lev}
                  onChange={(event, newValue) => onChange(newValue, "level")}
                  style={{ width: "auto" }}
                  renderInput={(params) => 
                                 <TextField 
                                    {...params} 
                                    label="Select Your Grade Level" 
                                    variant="outlined"
                                 />
                              }
               />
            </div>
            <div style={{display:"flex"}}>
               <Grid item sm={3}>
                  <p>School Registering</p>
               </Grid>

               <Autocomplete
                  options={options.school.map((option) => option.label)}
                  getOptionSelected={(option, value) => option.label === value.label}
                  value={choice.school}
                  onChange={(event, newValue) => onChange(newValue, "school")}
                  style={{ width: "auto" }}
                  renderInput={(params) => 
                                 <TextField 
                                    {...params} 
                                    label="Select Your School" 
                                    variant="outlined"
                                 />
                              }
               />
            </div>
            <div style={{display:"flex"}}>
               <Grid item sm={3}>
                  <p>Coach</p>
               </Grid>
               <TextField
                  variant="outlined" 
                  margin="normal" 
                  required
                  label="Coach"
                  value={choice.coach}
                  onChange={(event) => onChange(event, "coach")}
               >

               </TextField>
            </div>
         </form>
      </div>
   );
}

export default TeamRegister;
