import { TextField, Button, makeStyles, Grid } from "@material-ui/core";
import { Autocomplete } from "@material-ui/lab";
import React, { useState } from "react";

//Options for each dropdown. Probably use JSON for them

let options = require("./options");

const useStyles = makeStyles((theme) => ({
   root: {
     '& .MuiTextField-root': {
       margin: theme.spacing(1),
       width: '25ch',
     },
   },
 }));

//json
//value
//onChange
//text
//width
function Auto(props){
   return(
      <div style={{display:"flex"}} name={props.name}>
         <Grid item sm={3}>
            <p>{props.title}</p>
         </Grid>
         <Autocomplete
            options={props.options.map((option) => option.label)}
            value={props.value}
            onChange={props.onChange}
            freeSolo
            renderInput={(params) => 
                           <TextField 
                              {...params} 
                              error={props.error && (props.value === null || props.value === "")}
                              helperText={props.error && (props.value === null || props.value === "") ? 
                                             "Please fill out to continue" : 
                                             null
                                          }
                              label={props.text} 
                              variant="outlined"
                              required
                              style={{ width: props.width }}
                           />
                        }
         />
      </div>
   );
}

function LoginHome(){
   const classes = useStyles();
   const [choice, setChoice] = useState({ loc: null, 
                                          lev: null, 
                                          school: null,
                                          schoolData: {
                                            value: null,
                                            label: null,
                                            div: null,
                                          },
                                          team1: null,
                                          team2: null,
                                          coach: "",
                                          error: false,
                                       });
    var url = `https://docs.google.com/forms/d/e/1FAIpQLSeJ_fqnbSPgfrCmRAlcGN8lFCnLKw2zbvb8YUMRtYDjSTMXVQ/viewform?usp=pp_url
                &entry.296234163=${choice.loc}
                &entry.1262511676=${choice.lev}
                &entry.2068664503=${choice.schoolData.label}
                &entry.962283225=${choice.schoolData.value}
                &entry.1420093772=${choice.schoolData.div}
                &entry.75608970=${choice.team1}
                &entry.2041045214=${choice.team2}
                &entry.2062002355=${choice.coach}`

   let longest = 0;

   //finding length of longest string in options and resize search box accordingly
   for(var option in options){
      for(let i = 0; i < Object.keys(options[option]).length; i++){
         if(options[option][i].label.length > longest)
            longest = options[option][i].label.length;
      }
   }

   //don't know if there is a good way to do this, couldn't find anything
   longest *= 10;

   const onChange = (newValue, type) => {
      switch (type) {
         case "location":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  loc: newValue,
                  error: false,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  loc: null,
                  error: false,
               }));
            }
            break;
         case "level":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  lev: newValue,
                  error: false,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  lev: null,
                  error: false,
               }));
            }
            break;
         case "school":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  school: newValue,
                  error: false,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  school: null,
                  error: false,
               }));
            }
            break;
         case "team1":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  team1: newValue,
                  error: false,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  team1: null,
                  error: false,
               }));
            }
            break;
         case "team2":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  team2: newValue,
                  error: false,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  team2: null,
                  error: false,
               }));
            }
            break;
         case "coach":
            if(newValue != null){
               setChoice((prevState) => ({
                  ...prevState,
                  coach: newValue.target.value,
                  error: false,
               }));
            } else {
               setChoice((prevState) => ({
                  ...prevState,
                  coach: "",
                  error: false,
               }));
            }
            break;
         default:
            console.log(newValue, type)
      }
   };


   //NEED TO ADD SUBMISSION TO STORAGE
   const onSubmit = (event) => {
      
      //Setting error if something is not filled out.
      for (const item in choice){
         if(choice[item] === null || choice[item] === ""){
            console.log("ERROR :", item)
            setChoice((prevState) => ({
               ...prevState,
               error: true,
            }));
            return;
         }
      }

      //Getting all the data for that school
      for(option in options.school){
         if(options.school[option].label === choice.school){
            setChoice((prevState) => ({
               ...prevState,
               schoolData: {
                value: options.school[option].value,
                label: options.school[option].label,
                div: options.school[option].div,
               }
            }));
            console.log(options.school[option])
            break;
         }
      }

      //send data to sheets/database
      console.log(event)
      console.log(choice)

      // document.forms['test'].submit();
      //Supposed to send data to a Google Sheets macro that puts the data in the next available line
      event.preventDefault();
      const scriptURL = "https://script.google.com/macros/s/AKfycbzk2zetEGk2a7q1JDjwICJJsvLbeg4zkZGncVENK9ikYQdwCglwBvxKcIDystzv_3h1nw/exec";
      const form = document.forms["test"];
      var formData = new FormData(form);
      console.log(form)
      console.log(formData)
      fetch(scriptURL, { mode: "no-cors", method: "POST", body: formData})
        .then(response => console.log(response))
        .catch(error => console.log(error))
  
   }

   

   return(
      <div style={{display: "flex", flexDirection:"row"}}>
         <div style={{borderRadius: "4px", margin:"2%", boxShadow:"0 3px 1px -2px rgb(0 0 0 / 20%), 0 2px 2px 0 rgb(0 0 0 / 14%), 0 1px 5px 0 rgb(0 0 0 / 12%)"}}>
            <div style={{marginLeft:"1%", marginRight:"1%",}}>
               <form className={classes.root}>
                  
                  <Auto
                     title="Competition Location"
                     options={options.locations}
                     text="Select Competition Location"
                     onChange={(event, newValue) => onChange(newValue, "location")}
                     width={longest}
                     value={choice.loc}
                     error={choice.error}
                     name="Competition Location"
                  />
                  
                  <Auto
                     title="Competition Level"
                     options={options.level}
                     text="Select Your Grade Level"
                     onChange={(event, newValue) => onChange(newValue, "level")}
                     width={longest}
                     value={choice.lev}
                     error={choice.error}
                     name="Competition Level"
                  />

                  <Auto
                     title="School Registering"
                     options={options.school}
                     text="Select Your School"
                     onChange={(event, newValue) => onChange(newValue, "school")}
                     width={longest}
                     value={choice.school}
                     error={choice.error}
                     name="School Registering"
                  />

                  <Auto
                     title="Number of 4, 5, 6, 7, or 9-10th Teams"
                     options={options.numteam}
                     text="Select Number of Teams"
                     onChange={(event, newValue) => onChange(newValue, "team1")}
                     width={longest}
                     value={choice.team1}
                     error={choice.error}
                     name="Number of 4, 5, 6, 7, or 9-10th Teams"
                  />

                  <Auto
                     title="Number of 8 or 11-12th Teams"
                     options={options.numteam}
                     text="Select Number of Teams"
                     onChange={(event, newValue) => onChange(newValue, "team2")}
                     width={longest}
                     value={choice.team2}
                     error={choice.error}
                     name="Number of 8 or 11-12th Teams"
                  />
                  
                  <div style={{display:"flex"}} name="Person Bringing Students to Event">
                    <Grid item sm={3}>
                        <p>Person Bringing Students to Event</p>
                    </Grid>
                    <TextField
                        error={choice.error && choice.coach === ""}
                        helperText={choice.error && choice.coach === "" ? 
                                      "Please fill out to continue" : 
                                      null
                                    }
                        variant="outlined" 
                        margin="normal" 
                        required
                        label="Coach"
                        value={choice.coach}
                        onChange={(event) => onChange(event, "coach")}
                        style={{ width: longest }}
                    >
                    </TextField>
                  </div>

                  <div name="submit">
                    <Grid container>
                      <Grid item sm={3}></Grid>
                      <Grid item sm={3} width={longest}>
                          <Button
                            fullWidth
                            variant="contained"
                            color="primary"
                            onClick={onSubmit}
                            type="submit"
                          >
                            Submit
                          </Button>
                      </Grid>
                    </Grid>
                  </div>
                  
               </form>
               <form name="test" method="POST" action={url}>
                  <label for="loc">1</label>
                  <input type="text" id="loc" name="loc" value={choice.loc}/>
                  <label for="lev">2</label>
                  <input type="text" id="lev" name="lev" value={choice.lev}/>
                  <label for="label">3</label>
                  <input type="text" id="label" name="label" value={choice.schoolData.label}/>
                  <label for="val">4</label>
                  <input type="text" id="val" name="val" value={choice.schoolData.value}/>
                  <label for="div">5</label>
                  <input type="text" id="div" name="div" value={choice.schoolData.div}/>
                  <label for="team1">6</label>
                  <input type="text" id="team1" name="team1" value={choice.team1}/>
                  <label for="team2">7</label>
                  <input type="text" id="team2" name="team2" value={choice.team2}/>
                  <label for="coach">8</label>
                  <input type="text" id="coach" name="coach" value={choice.coach}/>
                </form>
            </div>
         </div>
      </div>
   );
}

export default LoginHome;
