import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

import Context from "./context/loginContext";
import useLoginState from "./context/useLoginState";

import fire from "./components/fire";
{/* <Context.Provider value={store}>
      <App />
    </Context.Provider> */}

const Index = () => {
  // const store = useLoginState();
  const [displayName, setName] = useState("");

  useEffect(() => {
    setTimeout(() => {setName(fire.auth().currentUser.displayName);}, 500)
  }, [displayName]);

  localStorage.setItem("name", displayName)
  localStorage.setItem("initials", localStorage.getItem("name").match(/(\b\S)?/g).join("").toUpperCase());

  return(
    <App />
  );
}

ReactDOM.render(
  <React.StrictMode>
    <Index />
  </React.StrictMode>,
  document.getElementById('root')
);

