//Wanted to add a onChange function for the registrations to make the code in each easier to read

function onChange(newValue, type, setChoice) {
  switch (type) {
    case "location":
      if(newValue != null){
        return setChoice((prevState) => ({
          ...prevState,
          loc: newValue,
          error: false,
        }));
      } else {
        return setChoice((prevState) => ({
          ...prevState,
          loc: null,
          error: false,
        }));
      }

    case "level":
      if(newValue != null){
        return setChoice((prevState) => ({
          ...prevState,
          lev: newValue,
          error: false,
        }));
      } else {
        return setChoice((prevState) => ({
          ...prevState,
          lev: null,
          error: false,
        }));
      }
      
    case "school":
