import React, { useState } from "react";
import { makeStyles, Drawer, Button } from "@material-ui/core";

import { Menu } from "@material-ui/icons"

import image from "../../assets/logo.5a82c15d88ad2d074447.png"

const drawerWidth = "128px";

const useStyles = makeStyles((theme) => ({
   closed:{
      transition: "margin-left 450ms cubic-bezier(0.23, 1, 0.32, 1)",
   },
   open:{
      transition:"margin-left 450ms cubic-bezier(0.23, 1, 0.32, 1)",
      width:`calc(100% - ${drawerWidth})`,
      marginLeft:drawerWidth,
   },
   button:{
      position:"relative",
      overflow:"hidden",
      display:"flex",
      flexDirection:"column",
      justifyContent:"left",
      color:"inherit",
      borderRadius:"5px",
      textDecoration:"none",
      textTransform:"uppercase",
      background:"transparent",
      border:"currentColor",
      fontSize:"15px",
      padding:"5px",
      '&:hover':{
      backgroundColor:"#2a3576",
      transition:"background-color 250ms \
                  cubic-bezier(0.4, 0, 0.2, 1) 0ms,box-shadow \
                  250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,border \
                  250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms",
    }
   }
}));

function SideBar() {
   const classes = useStyles();
   const [open, setOpen] = useState(false);
   
   return(
      <div>
         <Drawer open={open}>
            <div style={{background:`url(${image}) right center/contain no-repeat #3f51b5`}}>
               <div style={{marginLeft:drawerWidth}}>
                  <Button onClick={() => setOpen(false)} style={{color:"white"}}>X</Button>
               </div>
               <Button class={classes.button} href="/">
                  Home
               </Button>
            </div>
         </Drawer>
         <div class={open ? classes.open : classes.closed}>
            <Button onClick={() => setOpen(true)} style={{paddingTop:"10px"}}>
               <Menu/>
            </Button>
         </div>
      </div>
   );
};

export default SideBar;
