import React, { useState, useRef } from "react";
//import { Navbar, NavbarBrand, Nav, NavDropdown } from "react-bootstrap";
import { AppBar, Toolbar, Button, Typography, Menu, MenuItem, MenuList, Popper, Grow, ClickAwayListener, makeStyles } from "@material-ui/core";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  drop: {
    display:"flex",
    flexDirection:"column",
    backgroundColor:"white",
    borderRadius:"5px"
  },
  item: {
    color:"black"
  }
}))

function HeadBar() {
  const classes = useStyles();

  const [about, setAbout] = useState(null);
  const aboutRef = useRef(null);
  const [info, setInfo] = useState(null);
  const infoRef = useRef(null);
  const [resource, setResource] = useState(null);
  const resourceRef = useRef(null);

  const aboutClick = () => {
    setAbout((prevAbout) => !prevAbout);
  };

  const infoClick = (event) => {
    setInfo(event.currentTarget);
  };

  const resourceClick = (event) => {
    setResource(event.currentTarget);
  };

  const aboutClose = (event) => {
    if (aboutRef.current && aboutRef.current.contains(event.target)) {
      return;
    }

    setAbout(false);
  };

  const infoClose = () => {
    setInfo(null);
  };

  const resourceClose = () => {
    setResource(null);
  };

  const prevAbout = useRef(about);
  React.useEffect(() => {
    if (prevAbout.current === true && about === false) {
      aboutRef.current.focus();
    }

    prevAbout.current = about;
  }, [about]);

  return (
    <AppBar position="static">

      <Typography 
        style={{fontFamily:"cursive", 
                fontSize:"40px", 
                fontStyle:"italic", 
                marginLeft:"20px", 
                marginTop:"10px"}}
      >
              Math Is Cool
      </Typography>

      <Toolbar>
        <Button color="inherit" href="/">Home</Button>
      
        <div>
          <Button 
            ref={aboutRef}
            aria-controls={about ? 'menu-list-grow' : undefined}
            aria-haspopup="true"
            color="inherit" 
            onClick={aboutClick}
          >
            About Us
          </Button>
          <Popper open={about} anchorEl={aboutRef.current} role={undefined} transition disablePortal>
                  {({ TransitionProps, placement }) => (
                    <Grow
                      {...TransitionProps}
                      style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }}
                    >
                      <ClickAwayListener onClickAway={aboutClose}>
                        <MenuList class={classes.drop} autoFocusItem={about} id="menu-list-grow">
                          <MenuItem class={classes.item} component={Link} to="/about/history" onClick={aboutClose}>History</MenuItem>
                          <MenuItem class={classes.item} component={Link} to="/about/contacts" onClick={aboutClose}>Contacts</MenuItem>
                          <MenuItem class={classes.item} component={Link} to="/about/locations" onClick={aboutClose}>Locations</MenuItem>
                        </MenuList>
                      </ClickAwayListener>
                    </Grow>
                  )}
          </Popper>
        </div>

        <div>
          <Button aria-controls="fade-menu" aria-haspopup="true" color="inherit" onClick={infoClick}>Information</Button>
          <Menu
            id="fade-menu"
            anchorEl={info}
            keepMounted
            open={Boolean(info)}
            onClose={infoClose}
          >
            <MenuItem component={Link} to="/information/rules" onClick={infoClose}>Rules</MenuItem>
            <MenuItem component={Link} to="/information/fees" onClick={infoClose}>Fees</MenuItem>
            <MenuItem component={Link} to="/information/faq" onClick={infoClose}>FAQ</MenuItem>
          </Menu>
        </div>

        <div>
          <Button aria-controls="fade-menu" aria-haspopup="true" color="inherit" onClick={resourceClick}>Resources</Button>
          <Menu
            id="fade-menu"
            anchorEl={resource}
            keepMounted
            open={Boolean(resource)}
            onClose={resourceClose}
          >
            <MenuItem component={Link} to="/resources/rules" onClick={resourceClose}>Rules</MenuItem>
            <MenuItem component={Link} to="/resources/past-tests" onClick={resourceClose}>Past Tests</MenuItem>
          </Menu>
        </div>

        <Button color="inherit" href="/competitions">Competitions</Button>
        <Button color="inherit" href="/login">Login</Button>
      </Toolbar>
    </AppBar>
  );
}

export default HeadBar;
